Method.Implementation:
#dim parameterString as %String = ""
#dim k as %String = ""
set k = ""
while (1)
{
	#dim v as %String = a.GetNext(.k)
	quit:k=""  
	do ..AppendEncodedParameterToParameterString(.parameterString, k, v)
}
quit parameterString

if (k '= "") {
	if ($length(parameterString) > 0) {
		set parameterString = parameterString _ "&"
	} else {
		set parameterString = "?"
	}
	if (v = "") {
		set parameterString = parameterString _ ##class(Util.Misc).UrlEncode(k)
	} else {
		set parameterString = parameterString _ ##class(Util.Misc).UrlEncode(k) _ "=" _ ##class(Util.Misc).UrlEncode(v)
	}
}
