Method.Implementation:
	set result = "<tr scope=""row"">"
	set ptr = 0, i = 0
    while $listnext(contents, ptr, value) {
    	if (i = 0) {
			set result = result _ ..GetTableHeader(value)
		} else {
			set result = result _ ..GetTableData(value)
		}
		set i = i + 1
    }
    set result = result _ "</tr>"
    quit result

