#dim date As %Date = ""
if ($length(VKDate, ".") = 3) {
	if ($length($piece(VKDate, ".", 1)) = 1) {
		set VKDate = "0" _ VKDate
	}
	if ($length($piece(VKDate, ".", 2)) = 1) {
		set VKDate = $piece(VKDate, ".", 1) _ ".0" _ $piece(VKDate, ".", 2, 3)
	}
	set date = $zdateh($replace(VKDate, ".", "/"), 4)
}
quit date
